import React, { useState, useEffect } from 'react';
import './TodoBoard.css';

function TodoBoard() {
  const [todos, setTodos] = useState(() => {
    const stored = localStorage.getItem('todos');
    return stored ? JSON.parse(stored) : [];
  });

  const [newTodo, setNewTodo] = useState('');
  const [dueDate, setDueDate] = useState('');

  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const addTodo = () => {
    if (!newTodo.trim()) return;
    setTodos([...todos, { id: Date.now(), text: newTodo.trim(), dueDate, done: false }]);
    setNewTodo('');
    setDueDate('');
  };

  const toggleTodo = (id) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, done: !todo.done } : todo
    ));
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  return (
    <div className="todo-board">
      <h3 className="todo-header">üìù TODOs</h3>
      <div className="todo-inputs">
        <input
          type="text"
          value={newTodo}
          placeholder="What do you need to do?"
          onChange={(e) => setNewTodo(e.target.value)}
        />
        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <button onClick={addTodo}>Add</button>
      </div>
      <ul className="todo-list">
        {todos.map(todo => (
          <li key={todo.id} className={todo.done ? 'done' : ''}>
            <input
              type="checkbox"
              checked={todo.done}
              onChange={() => toggleTodo(todo.id)}
            />
            <span className="todo-text">{todo.text}</span>
            {todo.dueDate && <span className="due-date">üìÖ {todo.dueDate}</span>}
            <button className="delete" onClick={() => deleteTodo(todo.id)}>√ó</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoBoard;
