import React, { useState, useEffect } from 'react';
import './App.css';
import lofiBanner from './assets/lofi-girl.gif';
import CalendarEntryModal from './components/CalendarEntryModal';
import EventFormModal from './components/EventFormModal';
import TodoBoard from './components/TodoBoard';

const weekdays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
const monthNames = [
  'January', 'February', 'March', 'April', 'May', 'June',
  'July', 'August', 'September', 'October', 'November', 'December'
];

const emojiMap = {
  flight: '✈️',
  rent: '🏠',
  submission: '📄',
  outing: '🎉',
  call: '📞',
  other: '📝'
};

function App() {
  const today = new Date();
  const todayDate = today.getDate();
  const todayMonth = today.getMonth();
  const todayYear = today.getFullYear();

  const [year, setYear] = useState(todayYear);
  const [month, setMonth] = useState(todayMonth);
  const [entries, setEntries] = useState({});
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);
  const [formData, setFormData] = useState({});
  const [eventModalOpen, setEventModalOpen] = useState(false);
  const [eventForm, setEventForm] = useState({ title: '', date: '', type: '', notes: '' });
  const [events, setEvents] = useState({});
  const [hoveredEvent, setHoveredEvent] = useState(null);
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    const storedTodos = localStorage.getItem('todos');
    if (storedTodos) {
      setTodos(JSON.parse(storedTodos));
    }
  }, []);

  const firstDayOfMonth = new Date(year, month, 1);
  const startingDay = firstDayOfMonth.getDay();
  const daysInMonth = new Date(year, month + 1, 0).getDate();
  const daysInPrevMonth = new Date(year, month, 0).getDate();

  const goToPrevMonth = () => {
    if (month === 0) {
      setMonth(11);
      setYear(year - 1);
    } else {
      setMonth(month - 1);
    }
  };

  const goToNextMonth = () => {
    if (month === 11) {
      setMonth(0);
      setYear(year + 1);
    } else {
      setMonth(month + 1);
    }
  };

  const openModal = (day, type = 'current') => {
    const dateKey = type === 'prev'
      ? new Date(year, month - 1, day).toDateString()
      : type === 'next'
        ? new Date(year, month + 1, day).toDateString()
        : new Date(year, month, day).toDateString();
    setSelectedDate(dateKey);
    setFormData(entries[dateKey] || {});
    setModalOpen(true);
  };

  const saveEntry = () => {
    setEntries({ ...entries, [selectedDate]: formData });
    setModalOpen(false);
  };

  const saveEvent = () => {
    const { date, type, title, notes } = eventForm;
    if (!date || !type) return;

    const [inputYear, inputMonth, inputDay] = date.split('-').map(Number);
    const localDate = new Date(inputYear, inputMonth - 1, inputDay);
    const dateKey = localDate.toDateString();

    setEvents(prev => {
      const updated = [...(prev[dateKey] || []), { title, type, notes }];
      return { ...prev, [dateKey]: updated };
    });

    setEventModalOpen(false);
    setEventForm({ title: '', date: '', type: '', notes: '' });
  };

  const dayCells = [];

  for (let d = 1; d <= daysInMonth; d++) {
    const dateObj = new Date(year, month, d);
    const isoKey = dateObj.toISOString().split('T')[0];
    const dateKey = dateObj.toDateString();
    const isToday = d === todayDate && month === todayMonth && year === todayYear;
    const dayOfWeek = (startingDay + d - 1) % 7;
    const isSunday = dayOfWeek === 0;
    const isSaturday = dayOfWeek === 6;
    const dayClass = `day${isSunday ? ' sunday' : ''}${isSaturday ? ' saturday' : ''}${isToday ? ' today' : ''}`;
    const entry = entries[dateKey];
    const dayEvents = events[dateKey] || [];

    const dueToday = todos.filter(todo => todo.dueDate === isoKey);
    const allDoneOnTime =
      dueToday.length > 0 &&
      dueToday.every(todo => {
        if (!todo.done || !todo.completedDate) return false;
        return new Date(todo.completedDate) <= new Date(todo.dueDate);
      });

    dayCells.push(
      React.createElement("div", { className: dayClass, key: d, onClick: () => openModal(d) },
        React.createElement("div", { className: "day-number" }, d),
        allDoneOnTime && React.createElement("div", {
          className: "animated-star",
          style: { position: 'absolute', bottom: 6, left: 6, fontSize: '1rem' }
        }, "✨"),
        React.createElement("div", { className: "event-emojis" },
          dayEvents.map((event, idx) =>
            React.createElement("span", {
              key: idx,
              className: "event-emoji",
              onClick: (e) => {
                e.stopPropagation();
                setHoveredEvent({ dateKey, eventIndex: idx, ...event });
              }
            }, emojiMap[event.type] || '🗓️')
          )
        ),
        hoveredEvent?.dateKey === dateKey &&
        React.createElement("div", { className: "event-popup", onClick: (e) => e.stopPropagation() },
          React.createElement("div", { className: "popup-title" }, hoveredEvent.title),
          hoveredEvent.notes && React.createElement("div", { className: "popup-notes" }, hoveredEvent.notes),
          React.createElement("button", { className: "popup-close", onClick: () => setHoveredEvent(null) }, "×")
        ),
        entry && React.createElement("div", { className: "entry-preview" },
          React.createElement("div", null, `Coding: ${entry.coding?.questions_solved ?? '-'}`),
          React.createElement("div", null, `Thesis: ${entry.thesis?.contribution_score ?? '-'}`),
          React.createElement("div", null, `Studies: ${entry.studies?.study_score ?? '-'}`),
          React.createElement("div", null, `Project: ${entry.projects?.contribution_score ?? '-'}`)
        )
      )
    );
  }

  return (
    <div className="container">
      <div className="banner-strip">
        <img src={lofiBanner} alt="Lofi Banner" className="banner-gif" />
      </div>
      <div className="calendar-layout">
        <div className="calendar-column">
          <div className="calendar-header">
            <button onClick={goToPrevMonth} className="nav-button">❮</button>
            <h1 className="calendar-title">{monthNames[month]} {year}</h1>
            <button onClick={goToNextMonth} className="nav-button">❯</button>
          </div>
          <div className="weekday-row">
            {weekdays.map((day, index) => (
              <div className="weekday" key={index}>{day}</div>
            ))}
          </div>
          <div className="calendar-grid">
            {dayCells}
          </div>
        </div>
        <div className="sidebar-column">
          <TodoBoard />
        </div>
      </div>
    </div>
  );
}

export default App;
